import docx2dast from './docx2dast/docx2dast.js';
import dast2mdast from './dast2mdast/dast2mdast.js';
import mdast2markdown from './mdast2md/mdast2md.js';

/**
 * Converts a docx to markdown.
 *
 * @param {Buffer} doc - the Word document
 * @param {Docx2mdastOptions} [opts] options
 * @returns {Promise<string>} the markdown
 */
export default async function docx2md(doc, opts) {
  const { listener } = opts;
  if (listener) {
    listener('beforeParseDocx');
  }
  const dast = await docx2dast(doc, opts.parserOptions);

  if (listener) {
    listener('afterParseDocx', dast);
    listener('beforeToMdast', dast);
  }

  const mdast = await dast2mdast(dast, opts);

  if (listener) {
    listener('afterToMdast', mdast);
  }

  return mdast2markdown(mdast, opts);
}
