import { MediaHandler } from "@adobe/helix-mediahandler";
import {Docx2mdListener} from "../docx2md";

declare interface Logger {}

export declare interface MDast2mdOptions {

  log?: Logger,

  listener?: Docx2mdListener,

  mediaHandler?: MediaHandler,

  source?: string,

  gridtables?: boolean,
}

/**
 * Converts the mdast to markdown using common filters suitable for helix.
 *
 * @param {Node} mdast The mdast
 * @param {object} [opts] additional options
 * @param {MediaHandler} [opts.mediaHandler] blob handler for storing images
 * @param {string} [opts.source] the source document
 * @returns {Promise<string>} the markdown representation.
 */
export default function mdast2md(mdast: object, opts: MDast2mdOptions): Promise<string>;
