/**
 * Serialize a doctype.
 *
 * @param {Doctype} node
 *   xast doctype node.
 * @param {State} state
 *   Info passed around about the current state.
 * @returns {string}
 *   Serialized XML.
 */
export function doctype(node: Doctype, state: State): string;
export type Doctype = import('xast').Doctype;
export type State = import('./index.js').State;
